BEGIN TRANSACTION;
CREATE TABLE mpmc_book ("id" integer NOT NULL PRIMARY KEY , "title" varchar(200) NOT NULL, "author" varchar(200) NOT NULL, "language" VARCHAR(20) NULL, "genre" varchar(30),  "summary" text NOT NULL);
CREATE TABLE "mpmc_storico_bak"(
  id INT not null primary key,
  title TEXT,
  author TEXT,
  username TEXT,
  status TEXT,
  borrowed DATE,
  due_back DATE
);
CREATE TABLE "mpmc_user" (
"id" serial NOT NULL PRIMARY KEY, 
"first_name" varchar(150) NOT NULL,
"last_name" varchar(150) NOT NULL, 
"username" varchar(150) NOT NULL UNIQUE, 
"password" varchar(128), 
"email" varchar(254), 
"last_login" DATE NULL, 
"date_joined" DATE, 
"is_superuser" bool, 
"is_staff" bool, 
"is_active" bool
);
CREATE TABLE mpmc_storico (
	id INTEGER NOT NULL, 
	title VARCHAR(100), 
	author VARCHAR(100), 
	username VARCHAR(100), 
	status VARCHAR(1), 
	borrowed DATE, 
	due_back DATE, 
	PRIMARY KEY (id)
);
CREATE INDEX mpmc_book_auth on mpmc_book(author);
CREATE INDEX ix_mpmc_storico_id ON mpmc_storico (id);
COMMIT;
