INSERT INTO mpmc_book VALUES(31,'L'' Amo e la lenza','Mario,Albertarelli','Italiano','Storytelling','Questo libro per un appassionato è una bibbia e se si trova va assolutante acquistato e tenuto sul comodino. Non rimpiangerete i vostri soldi perché questo romanzo di pesca e di vita vissuta vi farà sognare nei ricordi più belli');
